/* globals window, document, console */

import BBSMEditor from '../../build/ckeditor';

BBSMEditor.create( document.querySelector( '#editor' ) )
	.then( editor => {
		window.editor = editor;
	} )
	.catch( error => {
		console.error( 'There was a problem initializing the editor.', error );
	} );
